import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

const Navbar = () => {
  const navigate = useNavigate();

  // Manage token state dynamically
  const [token, setToken] = useState(localStorage.getItem("token"));

  useEffect(() => {
    // Update token when it changes in localStorage
    const handleStorageChange = () => {
      setToken(localStorage.getItem("token"));
    };

    // Listen for changes to localStorage
    window.addEventListener("storage", handleStorageChange);

    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    setToken(null); // Remove token from state immediately
    navigate("/");
  };

  const handleContactsClick = () => {
    if (token) {
      navigate("/contacts");
    } else {
      alert("Please login to access Contacts.");
      navigate("/login");
    }
  };

  return (
    <nav className="bg-gray-800 p-4 shadow-md fixed w-full z-50">
      <div className="max-w-screen-xl mx-auto flex justify-between items-center">
        <div className="text-white text-2xl font-extrabold">
          <Link to="/">Zenstudy</Link>
        </div>
        <ul className="flex space-x-6 text-white">
          <li>
            <Link
              to="/"
              className="hover:text-gray-400 transition duration-300"
            >
              Home
            </Link>
          </li>
          {!token ? (
            <>
              <li>
                <Link
                  to="/login"
                  className="hover:text-gray-400 transition duration-300"
                >
                  Login
                </Link>
              </li>
              <li>
                <Link
                  to="/register"
                  className="hover:text-gray-400 transition duration-300"
                >
                  Register
                </Link>
              </li>
            </>
          ) : (
            <>
              <li>
                <Link
                  to="/dashboard"
                  className="hover:text-gray-400 transition duration-300"
                >
                  Dashboard
                </Link>
              </li>
              <li>
                <button
                  onClick={handleLogout}
                  className="hover:text-gray-400 transition duration-300"
                >
                  Logout
                </button>
              </li>
            </>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
