// export default Dashboard;
import React, { useState } from "react";
import ShowContacts from "./ShowContacts";
import AddContact from "./AddContact";

const Dashboard = () => {
  const [activeComponent, setActiveComponent] = useState("show");

  const renderComponent = () => {
    switch (activeComponent) {
      case "show":
        return <ShowContacts />;
      case "add":
        return <AddContact />;
      default:
        return <ShowContacts />;
    }
  };

  return (
    <div className="flex min-h-screen bg-gradient-to-r from-teal-100 via-teal-200 to-teal-300">
      {/* Sidebar */}
      <div className="w-64 bg-white text-gray-800 p-6 rounded-lg shadow-lg">
        <h2 className="text-2xl font-extrabold mb-8 text-teal-600">
          Dashboard
        </h2>
        <ul>
          <li
            className={`cursor-pointer py-3 px-4 rounded-lg mb-4 hover:bg-teal-100 transition duration-200 ${
              activeComponent === "show" ? "bg-teal-200" : ""
            }`}
            onClick={() => setActiveComponent("show")}
          >
            Contacts
          </li>
          <li
            className={`cursor-pointer py-3 px-4 rounded-lg mb-4 hover:bg-teal-100 transition duration-200 ${
              activeComponent === "add" ? "bg-teal-200" : ""
            }`}
            onClick={() => setActiveComponent("add")}
          >
            Add Contact
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8 bg-white rounded-lg shadow-lg mx-4 mt-6">
        {renderComponent()}
      </div>
    </div>
  );
};

export default Dashboard;
