import React, { useEffect, useState } from "react";
import API from "../services/api"; // Adjust path as needed

const ShowContacts = () => {
  const [contacts, setContacts] = useState([]);
  const [form, setForm] = useState({ name: "", mobile: "", email: "" });
  const [editingId, setEditingId] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch contacts from the backend
  const fetchContacts = async () => {
    try {
      const { data } = await API.get("/contacts");
      setContacts(data);
    } catch (error) {
      alert("Failed to fetch contacts");
    }
  };

  useEffect(() => {
    fetchContacts();
  }, []);

  // Handle form submission for adding or editing a contact
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editingId) {
        // If editing, update the contact
        await API.put(`/contacts/${editingId}`, form);
        alert("Contact updated successfully");
      } else {
        // If adding, create a new contact
        await API.post("/contacts", form);
        alert("Contact added successfully");
      }
      setForm({ name: "", mobile: "", email: "" });
      setEditingId(null);
      setIsModalOpen(false);
      fetchContacts(); // Refresh contact list
    } catch (error) {
      alert("Failed to save contact");
    }
  };

  // Handle edit button click
  const handleEdit = (contact) => {
    setForm(contact);
    setEditingId(contact._id);
    setIsModalOpen(true); // Open the modal for editing
  };

  // Handle delete button click
  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this contact?")) {
      try {
        const response = await API.delete(`/contacts/${id}`);
        if (response.status === 200) {
          alert("Contact deleted successfully");
          fetchContacts(); // Refresh the list after deletion
        }
      } catch (error) {
        alert("Failed to delete contact");
        console.error(error);
      }
    }
  };

  // Close the modal without making any changes
  const closeModal = () => {
    setIsModalOpen(false);
    setForm({ name: "", mobile: "", email: "" });
    setEditingId(null); // Reset the editing state
  };

  return (
    <div className="show-contacts">
      <h1>Show Contacts</h1>

      {/* List all contacts */}
      <ul>
        {contacts.map((contact) => (
          <li key={contact._id}>
            {contact.name} - {contact.mobile} - {contact.email}
            <button onClick={() => handleEdit(contact)}>Edit</button>
            <button onClick={() => handleDelete(contact._id)}>Delete</button>
          </li>
        ))}
      </ul>

      {/* Modal for adding or editing a contact */}
      {isModalOpen && (
        <div className="modal-overlay">
          <div className="modal">
            <button className="close-btn" onClick={closeModal}>
              ✖
            </button>
            <h2>{editingId ? "Edit" : "Add"} Contact</h2>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                placeholder="Name"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
              />
              <input
                type="text"
                placeholder="Mobile"
                value={form.mobile}
                onChange={(e) => setForm({ ...form, mobile: e.target.value })}
              />
              <input
                type="email"
                placeholder="Email"
                value={form.email}
                onChange={(e) => setForm({ ...form, email: e.target.value })}
              />
              <button type="submit">
                {editingId ? "Update" : "Add"} Contact
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ShowContacts;
