import React, { useState } from "react";
import ShowContacts from "./ShowContacts";
import AddContact from "./AddContact";

const Dashboard = () => {
  const [activeComponent, setActiveComponent] = useState("show");

  const renderComponent = () => {
    switch (activeComponent) {
      case "show":
        return <ShowContacts />;
      case "add":
        return <AddContact />;
      default:
        return <ShowContacts />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      {/* Fixed Navbar */}
      <nav className="bg-gray-800 p-4 shadow-md fixed w-full z-50 top-0 left-0">
        <div className="flex items-center justify-between max-w-screen-xl mx-auto">
          <div className="text-xl font-bold text-white">Zenstudy</div>
          <ul className="flex space-x-6">
            <li className="hover:bg-teal-700 px-4 py-2 rounded-lg">
              <a href="/">Home</a>
            </li>
            <li className="hover:bg-teal-700 px-4 py-2 rounded-lg">
              <a href="/dashboard">Dashboard</a>
            </li>
            <li className="hover:bg-teal-700 px-4 py-2 rounded-lg">
              <a href="/login">Login</a>
            </li>
            <li className="hover:bg-teal-700 px-4 py-2 rounded-lg">
              <a href="/register">Register</a>
            </li>
          </ul>
        </div>
      </nav>

      {/* Main Content */}
      <div className="flex flex-1 mt-16">
        {/* Sidebar */}
        <div className="w-64 bg-white text-gray-800 p-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-extrabold mb-8 text-gray-800">
            Dashboard
          </h2>
          <ul>
            <li
              className={`cursor-pointer py-3 px-4 rounded-lg mb-4 hover:bg-teal-100 transition duration-200 ${
                activeComponent === "show" ? "bg-teal-200" : ""
              }`}
              onClick={() => setActiveComponent("show")}
            >
              Contacts
            </li>
            <li
              className={`cursor-pointer py-3 px-4 rounded-lg mb-4 hover:bg-teal-100 transition duration-200 ${
                activeComponent === "add" ? "bg-teal-200" : ""
              }`}
              onClick={() => setActiveComponent("add")}
            >
              Add Contact
            </li>
          </ul>
        </div>

        {/* Main Content Area */}
        <div className="flex-1 p-8 bg-white rounded-lg shadow-lg mx-4 mt-6">
          {renderComponent()}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
