import React, { useState } from "react";
import API from "../services/api"; // Import your API instance

const FindContacts = () => {
  const [query, setQuery] = useState("");
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Handle search input change
  const handleSearchChange = (e) => {
    setQuery(e.target.value);
  };

  // Fetch contacts based on search query
  const searchContacts = async () => {
    if (!query.trim()) {
      alert("Please enter a search query.");
      return;
    }

    setLoading(true);
    setError(null); // Reset error state

    try {
      const token = localStorage.getItem("token");

      if (!token) {
        alert("No token found. Please log in.");
        return;
      }

      const response = await API.get(`/contacts/search?query=${query}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setContacts(response.data); // Set contacts to the search results
      setLoading(false);
    } catch (err) {
      setLoading(false);
      setError("Failed to find contacts. Please try again.");
    }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        Find Contacts
      </h2>

      {/* Search input */}
      <input
        type="text"
        value={query}
        onChange={handleSearchChange}
        placeholder="Search by name or mobile number"
        style={{
          width: "100%",
          padding: "10px",
          marginBottom: "20px",
          borderRadius: "4px",
          border: "1px solid #ccc",
        }}
      />

      {/* Search button */}
      <button
        onClick={searchContacts}
        style={{
          padding: "10px 20px",
          borderRadius: "4px",
          border: "1px solid #ccc",
          backgroundColor: "#4CAF50",
          color: "white",
          cursor: "pointer",
        }}
      >
        Search
      </button>

      {/* Loading state */}
      {loading && <p>Loading...</p>}

      {/* Error message */}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Display search results */}
      {contacts.length > 0 ? (
        <table
          style={{
            width: "100%",
            borderCollapse: "collapse",
            marginTop: "20px",
          }}
        >
          <thead>
            <tr style={{ backgroundColor: "#f4f4f4", textAlign: "left" }}>
              <th style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                Name
              </th>
              <th style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                Mobile
              </th>
              <th style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                Email
              </th>
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact) => (
              <tr key={contact._id}>
                <td style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                  {contact.name}
                </td>
                <td style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                  {contact.mobile}
                </td>
                <td style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                  {contact.email}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No contacts found matching your search query.</p>
      )}
    </div>
  );
};

export default FindContacts;
