import React, { useState } from "react";
import ShowContacts from "./ShowContacts";
import AddContact from "./AddContact";

const Dashboard = () => {
  const [activeComponent, setActiveComponent] = useState("show");
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const renderComponent = () => {
    switch (activeComponent) {
      case "show":
        return <ShowContacts />;
      case "add":
        return <AddContact />;
      default:
        return <ShowContacts />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col lg:flex-row">
      {/* Toggle Button for Mobile */}
      <div className="lg:hidden bg-teal-500 text-white p-3 shadow-md flex justify-between items-center">
        <h2 className="text-xl font-bold">Dashboard</h2>
        <button
          onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          className="text-white bg-teal-600 hover:bg-teal-700 p-2 rounded-md"
        >
          {isSidebarOpen ? "Close Menu" : "Open Menu"}
        </button>
      </div>

      {/* Sidebar */}
      <div
        className={`${
          isSidebarOpen ? "block" : "hidden"
        } lg:block w-full lg:w-64 bg-white text-gray-800 p-6 rounded-lg shadow-md lg:fixed lg:top-0 lg:left-0 lg:bottom-0`}
      >
        <h2 className="text-2xl font-extrabold mb-8 text-gray-800">
          Dashboard
        </h2>
        <ul>
          <li
            className={`cursor-pointer py-3 px-4 rounded-lg mb-4 hover:bg-teal-100 transition duration-200 ${
              activeComponent === "show" ? "bg-teal-200" : ""
            }`}
            onClick={() => {
              setActiveComponent("show");
              setIsSidebarOpen(false); // Close sidebar on mobile
            }}
          >
            Contacts
          </li>
          <li
            className={`cursor-pointer py-3 px-4 rounded-lg mb-4 hover:bg-teal-100 transition duration-200 ${
              activeComponent === "add" ? "bg-teal-200" : ""
            }`}
            onClick={() => {
              setActiveComponent("add");
              setIsSidebarOpen(false); // Close sidebar on mobile
            }}
          >
            Add Contact
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div
        className={`flex-1 p-8 bg-white rounded-lg shadow-lg mx-4 mt-6 ${
          isSidebarOpen ? "hidden lg:block" : "block"
        } lg:ml-72`}
      >
        {renderComponent()}
      </div>
    </div>
  );
};

export default Dashboard;
