import React, { useState } from "react";
import ShowContacts from "./ShowContacts";
import AddContact from "./AddContact";

const Dashboard = () => {
  const [activeComponent, setActiveComponent] = useState("show");

  const renderComponent = () => {
    switch (activeComponent) {
      case "show":
        return <ShowContacts />;
      case "add":
        return <AddContact />;
      default:
        return <ShowContacts />;
    }
  };

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-indigo-600 text-white p-6">
        <h2 className="text-2xl font-bold mb-8">Dashboard</h2>
        <ul>
          <li
            className={`cursor-pointer py-2 px-4 rounded-lg mb-4 hover:bg-indigo-700 transition duration-200 ${activeComponent === "show" ? "bg-indigo-700" : ""}`}
            onClick={() => setActiveComponent("show")}
          >
            Contacts
          </li>
          <li
            className={`cursor-pointer py-2 px-4 rounded-lg mb-4 hover:bg-indigo-700 transition duration-200 ${activeComponent === "add" ? "bg-indigo-700" : ""}`}
            onClick={() => setActiveComponent("add")}
          >
            Add Contact
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8 bg-white">
        {renderComponent()}
      </div>
    </div>
  );
};

export default Dashboard;
