import React, { useState } from "react";
import ShowContacts from "./ShowContacts";
import AddContact from "./AddContact";

const Dashboard = () => {
  const [activeComponent, setActiveComponent] = useState("show");
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const renderComponent = () => {
    switch (activeComponent) {
      case "show":
        return <ShowContacts />;
      case "add":
        return <AddContact />;
      default:
        return <ShowContacts />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col lg:flex-row">
      {/* Toggle button for small screens */}
      <button
        className="lg:hidden bg-teal-500 text-white p-3 m-4 rounded-lg shadow-md"
        onClick={() => setIsSidebarOpen(!isSidebarOpen)}
      >
        {isSidebarOpen ? "Close Menu" : "Open Menu"}
      </button>

      {/* Sidebar */}
      <div
        className={`${
          isSidebarOpen ? "block" : "hidden"
        } lg:block w-full lg:w-64 bg-white text-gray-800 p-6 rounded-lg shadow-md lg:fixed lg:top-0 lg:left-0 lg:bottom-0`}
      >
        <h2 className="text-2xl font-extrabold mb-8 text-gray-800">
          Dashboard
        </h2>
        <ul>
          <li
            className={`cursor-pointer py-3 px-4 rounded-lg mb-4 hover:bg-teal-100 transition duration-200 ${
              activeComponent === "show" ? "bg-teal-200" : ""
            }`}
            onClick={() => {
              setActiveComponent("show");
              setIsSidebarOpen(false); // Close sidebar on small screens
            }}
          >
            Contacts
          </li>
          <li
            className={`cursor-pointer py-3 px-4 rounded-lg mb-4 hover:bg-teal-100 transition duration-200 ${
              activeComponent === "add" ? "bg-teal-200" : ""
            }`}
            onClick={() => {
              setActiveComponent("add");
              setIsSidebarOpen(false); // Close sidebar on small screens
            }}
          >
            Add Contact
          </li>
        </ul>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-8 bg-white rounded-lg shadow-lg mx-4 mt-6 lg:ml-72">
        {renderComponent()}
      </div>
    </div>
  );
};

export default Dashboard;
