import React, { useEffect, useState } from "react";
import API from "../services/api";

const ShowContacts = () => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch contacts from backend
  const fetchContacts = async () => {
    try {
      const { data } = await API.get("/contacts"); // Make sure the backend includes _id
      setContacts(data);
    } catch (error) {
      alert("Failed to fetch contacts");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchContacts();
  }, []);

  // Delete contact by ID
  const handleDelete = async (id) => {
    if (window.confirm("Are you sure you want to delete this contact?")) {
      try {
        await API.delete(`/contacts/${id}`); // Send ID to delete the contact
        alert("Contact deleted successfully");
        fetchContacts(); // Refresh the contacts list
      } catch (error) {
        alert("Failed to delete contact");
      }
    }
  };

  // Edit contact (this can open a modal or a form for updating)
  const handleEdit = (contact) => {
    // You can implement the modal or update form logic here
    console.log("Edit contact:", contact);
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Show Contacts</h2>
      {contacts.length > 0 ? (
        <ul>
          {contacts.map((contact) => (
            <li key={contact._id}>
              <strong>{contact.name}</strong> - {contact.mobile} -{" "}
              {contact.email}
              <button onClick={() => handleEdit(contact)}>Edit</button>
              <button onClick={() => handleDelete(contact._id)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No contacts found.</p>
      )}
    </div>
  );
};

export default ShowContacts;
