import React, { useEffect, useState } from "react";
import API from "../services/api"; // Import the API instance

const ShowContacts = () => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch contacts from the backend
  const fetchContacts = async () => {
    try {
      const token = localStorage.getItem("token"); // Replace with your token storage mechanism
      const response = await API.get("/contacts", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setContacts(response.data.data || response.data); // Adjust based on backend response
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch contacts. Please try again.");
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchContacts();
  }, []);

  if (loading) return <p>Loading contacts...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "auto" }}>
      <h2 style={{ textAlign: "center", marginBottom: "20px" }}>
        Your Contacts
      </h2>
      {contacts.length > 0 ? (
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ backgroundColor: "#f4f4f4", textAlign: "left" }}>
              <th style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                Name
              </th>
              <th style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                Mobile
              </th>
              <th style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                Email
              </th>
            </tr>
          </thead>
          <tbody>
            {contacts.map((contact) => (
              <tr key={contact._id}>
                <td style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                  {contact.name}
                </td>
                <td style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                  {contact.mobile}
                </td>
                <td style={{ padding: "10px", borderBottom: "1px solid #ccc" }}>
                  {contact.email}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No contacts found.</p>
      )}
    </div>
  );
};

export default ShowContacts;
