import React, { useEffect, useState } from "react";
import API from "../services/api";

const ShowContacts = () => {
  const [contacts, setContacts] = useState([]);
  const [form, setForm] = useState({ name: "", mobile: "", email: "" });
  const [editingId, setEditingId] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch contacts from the backend
  const fetchContacts = async () => {
    try {
      const { data } = await API.get("/contacts");
      setContacts(data);
    } catch (error) {
      alert("Failed to fetch contacts");
    }
  };

  useEffect(() => {
    fetchContacts();
  }, []);

  // Handle form submission for adding or editing a contact
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editingId) {
        await API.put(`/contacts/${editingId}`, form); // Edit contact
        alert("Contact updated successfully");
      } else {
        await API.post("/contacts", form); // Add new contact
        alert("Contact added successfully");
      }
      setForm({ name: "", mobile: "", email: "" }); // Clear form
      setEditingId(null); // Reset editing state
      setIsModalOpen(false); // Close modal
      fetchContacts(); // Refresh contacts list
    } catch (error) {
      alert("Failed to save contact");
    }
  };

  // Handle edit button click (opens the modal with pre-filled data)
  const handleEdit = (contact) => {
    setForm(contact); // Populate the form with contact data
    setEditingId(contact._id); // Set the editing ID
    setIsModalOpen(true); // Open the modal
  };

  // Handle delete button click
  const handleDelete = async (id) => {
    try {
      await API.delete(`/contacts/${id}`); // Delete contact
      alert("Contact deleted successfully");
      fetchContacts(); // Refresh contacts list
    } catch (error) {
      alert("Failed to delete contact");
    }
  };

  return (
    <div className="show-contacts">
      <h1>Show Contacts</h1>
      {/* Display the contacts in a list */}
      <ul>
        {contacts.map((contact) => (
          <li key={contact._id}>
            {contact.name} - {contact.mobile} - {contact.email}
            <button onClick={() => handleEdit(contact)}>Edit</button>
            <button onClick={() => handleDelete(contact._id)}>Delete</button>
          </li>
        ))}
      </ul>

      {/* Modal for editing contact */}
      {isModalOpen && (
        <div className="modal">
          <h2>{editingId ? "Edit" : "Add"} Contact</h2>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Name"
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
            />
            <input
              type="text"
              placeholder="Mobile"
              value={form.mobile}
              onChange={(e) => setForm({ ...form, mobile: e.target.value })}
            />
            <input
              type="email"
              placeholder="Email"
              value={form.email}
              onChange={(e) => setForm({ ...form, email: e.target.value })}
            />
            <button type="submit">
              {editingId ? "Update" : "Add"} Contact
            </button>
          </form>
          <button onClick={() => setIsModalOpen(false)}>Close</button>
        </div>
      )}
    </div>
  );
};

export default ShowContacts;
